mapper.select:
SELECT count(*)
FROM
   (
    SELECT passageway_id as passagewayId
    FROM dp_open_pay_order_result
    where pay_time >= #{beginTime}
      and pay_time <= #{endTime}
      and status in('0','1','4')
      and (imei is not null or imei != '')
      
          and net_type = #{netType}
      
      
          and app_id = #{appId}
      
      
          and province = #{province}
      
      
          and passageway_id = #{passagewayId}
      
    group by passageway_id
   ) a

SELECT a.passagewayId, c.passageway_name as passagewayName, a.requestCount, a.confirmCount, a.successCount, a.riskCount, COALESCE(b.mrCount,0) as mrCount, CASE WHEN a.confirmCount > 0 THEN ROUND(a.successCount::numeric/a.confirmCount::numeric,4) ELSE 0 END as successRate, CASE WHEN a.successCount > 0 THEN ROUND(COALESCE(b.mrCount,0)::numeric/a.successCount::numeric,4) ELSE 0 END as syncRate FROM ( SELECT passageway_id as passagewayId, count(distinct(CASE WHEN status in('0','1','4') THEN imei END)) AS requestCount, count(distinct(CASE WHEN status in('0','1') THEN imei END)) AS confirmCount, count(distinct(CASE WHEN status ='1' THEN imei END)) AS successCount, count(distinct(CASE WHEN status ='4' THEN imei END)) AS riskCount FROM dp_open_pay_order_result where pay_time >= #{beginTime} and pay_time <= #{endTime} and status in('0','1','4') and (imei is not null or imei != '')
and net_type = #{netType}
and app_id = #{appId}
and province = #{province}
and passageway_id = #{passagewayId}
group by passageway_id ) a LEFT JOIN ( SELECT bx.sp_code, count(*) as mrCount from ( select DISTINCT linkid,sp_code,pay_money,status,mobile,long_num from dp_third_note_pay_result where create_time >= #{beginTime} and create_time <= #{endTime} and status_desc = '1'
and sp_code = #{passagewayId}
) bx group by bx.sp_code ) b ON a.passagewayId = b.sp_code LEFT JOIN dp_note_info c ON a.passagewayId = c.passageway_id order by successRate desc limit #{rowsPerPage} offset #{start}

SELECT count(*)
FROM
   (
    SELECT net_type,
           province
    FROM dp_open_pay_order_result
    where pay_time >= #{beginTime}
      and pay_time <= #{endTime}
      and status in('0','1','2','3','4')
      and (imei is not null or imei != '')
      
          and net_type = #{netType}
      
      
          and province = #{province}
      
    group by net_type,province
   ) a

SELECT a.province, a.netType, a.requestCount, a.confirmCount, a.successCount, a.riskCount, CASE WHEN a.confirmCount > 0 THEN ROUND(a.successCount::numeric/confirmCount::numeric,4) ELSE 0 END as successRate FROM ( SELECT net_type as netType, province, count(distinct(CASE WHEN status in('0','1','2','3','4') THEN imei END)) AS requestCount, count(distinct(CASE WHEN status in('0','1') THEN imei END)) AS confirmCount, count(distinct(CASE WHEN status ='1' THEN imei END)) AS successCount, count(distinct(CASE WHEN status ='4' THEN imei END)) AS riskCount FROM dp_open_pay_order_result where pay_time >= #{beginTime} and pay_time <= #{endTime} and status in('0','1','2','3','4') and (imei is not null or imei != '')
and net_type = #{netType}
and province = #{province}
group by net_type,province ) a order by successRate desc limit #{rowsPerPage} offset #{start}

select count(*)
from
   (
    select sp_code as passagewayId
    from dp_third_note_pay_result
    where create_time >= #{beginTime}
      and create_time <= #{endTime}
      and status_desc = '1'
      
          and sp_code = #{passagewayId}
      
    group by passagewayId
   ) a

select a.statisDate, a.passagewayId, b.passageway_name as passagewayName, a.fee from ( select to_char(create_time, 'yyyy-MM-dd') as statisDate, sp_code as passagewayId, sum(pay_money/100) as fee from dp_third_note_pay_result where create_time >= #{beginTime} and create_time <= #{endTime} and status_desc = '1'
and sp_code = #{passagewayId}
group by statisDate,passagewayId order by fee desc ) a LEFT JOIN dp_note_info b ON a.passagewayId = b.passageway_id limit #{rowsPerPage} offset #{start}
