mapper.select:
		select count(*)
		from dp_pop_up_info
		where 1 = 1
		
            and plugin_id = #{passagewayId}
        
        
            and net_type = #{netType}
        
        
            and channel_no = #{channelNo}
        
        
            and province = #{province}

		select *
		from dp_pop_up_info
        where 1 = 1
        
            and plugin_id = #{passagewayId}
        
        
            and net_type = #{netType}
        
        
            and channel_no = #{channelNo}
        
        
            and province = #{province}
        
		order by create_time desc
		limit #{rowsPerPage} offset #{start}
